int main(int argc, char **argv) {

    ros::init(argc, argv, "riobridge");

    data = (data_t*)malloc(sizeof(data_t));
    register_signals();
    interface_init();

    ros::NodeHandle nSIGOUT_BOARD0_BOARD0_WLED_0_GREEN;
    ros::Subscriber sub_SIGOUT_BOARD0_BOARD0_WLED_0_GREEN = nSIGOUT_BOARD0_BOARD0_WLED_0_GREEN.subscribe("board0/board0/board0_wled/0_green", 1000, cb_SIGOUT_BOARD0_BOARD0_WLED_0_GREEN);
    ros::NodeHandle nSIGOUT_BOARD0_BOARD0_WLED_0_BLUE;
    ros::Subscriber sub_SIGOUT_BOARD0_BOARD0_WLED_0_BLUE = nSIGOUT_BOARD0_BOARD0_WLED_0_BLUE.subscribe("board0/board0/board0_wled/0_blue", 1000, cb_SIGOUT_BOARD0_BOARD0_WLED_0_BLUE);
    ros::NodeHandle nSIGOUT_BOARD0_BOARD0_WLED_0_RED;
    ros::Subscriber sub_SIGOUT_BOARD0_BOARD0_WLED_0_RED = nSIGOUT_BOARD0_BOARD0_WLED_0_RED.subscribe("board0/board0/board0_wled/0_red", 1000, cb_SIGOUT_BOARD0_BOARD0_WLED_0_RED);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR0_VELOCITY;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR0_VELOCITY = nSIGOUT_BOARD0_STEPDIR0_VELOCITY.subscribe("board0/board0/stepdir0/velocity", 1000, cb_SIGOUT_BOARD0_STEPDIR0_VELOCITY);
    ros::NodeHandle nSIGIN_BOARD0_STEPDIR0_POSITION;
    std_msgs::Float32 msg_SIGIN_BOARD0_STEPDIR0_POSITION;
    ros::Publisher pub_SIGIN_BOARD0_STEPDIR0_POSITION = nSIGIN_BOARD0_STEPDIR0_POSITION.advertise<std_msgs::Float32>("board0/board0/stepdir0/position", 1000);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR0_ENABLE;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR0_ENABLE = nSIGOUT_BOARD0_STEPDIR0_ENABLE.subscribe("board0/board0/stepdir0/enable", 1000, cb_SIGOUT_BOARD0_STEPDIR0_ENABLE);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR1_VELOCITY;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR1_VELOCITY = nSIGOUT_BOARD0_STEPDIR1_VELOCITY.subscribe("board0/board0/stepdir1/velocity", 1000, cb_SIGOUT_BOARD0_STEPDIR1_VELOCITY);
    ros::NodeHandle nSIGIN_BOARD0_STEPDIR1_POSITION;
    std_msgs::Float32 msg_SIGIN_BOARD0_STEPDIR1_POSITION;
    ros::Publisher pub_SIGIN_BOARD0_STEPDIR1_POSITION = nSIGIN_BOARD0_STEPDIR1_POSITION.advertise<std_msgs::Float32>("board0/board0/stepdir1/position", 1000);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR1_ENABLE;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR1_ENABLE = nSIGOUT_BOARD0_STEPDIR1_ENABLE.subscribe("board0/board0/stepdir1/enable", 1000, cb_SIGOUT_BOARD0_STEPDIR1_ENABLE);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR2_VELOCITY;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR2_VELOCITY = nSIGOUT_BOARD0_STEPDIR2_VELOCITY.subscribe("board0/board0/stepdir2/velocity", 1000, cb_SIGOUT_BOARD0_STEPDIR2_VELOCITY);
    ros::NodeHandle nSIGIN_BOARD0_STEPDIR2_POSITION;
    std_msgs::Float32 msg_SIGIN_BOARD0_STEPDIR2_POSITION;
    ros::Publisher pub_SIGIN_BOARD0_STEPDIR2_POSITION = nSIGIN_BOARD0_STEPDIR2_POSITION.advertise<std_msgs::Float32>("board0/board0/stepdir2/position", 1000);
    ros::NodeHandle nSIGOUT_BOARD0_STEPDIR2_ENABLE;
    ros::Subscriber sub_SIGOUT_BOARD0_STEPDIR2_ENABLE = nSIGOUT_BOARD0_STEPDIR2_ENABLE.subscribe("board0/board0/stepdir2/enable", 1000, cb_SIGOUT_BOARD0_STEPDIR2_ENABLE);


    ros::Rate loop_rate(10);

    while (ros::ok()) {
        rio_readwrite(NULL, 0);

        msg_SIGIN_BOARD0_STEPDIR0_POSITION.data = *data->SIGIN_BOARD0_STEPDIR0_POSITION;
        pub_SIGIN_BOARD0_STEPDIR0_POSITION.publish(msg_SIGIN_BOARD0_STEPDIR0_POSITION);

        msg_SIGIN_BOARD0_STEPDIR1_POSITION.data = *data->SIGIN_BOARD0_STEPDIR1_POSITION;
        pub_SIGIN_BOARD0_STEPDIR1_POSITION.publish(msg_SIGIN_BOARD0_STEPDIR1_POSITION);

        msg_SIGIN_BOARD0_STEPDIR2_POSITION.data = *data->SIGIN_BOARD0_STEPDIR2_POSITION;
        pub_SIGIN_BOARD0_STEPDIR2_POSITION.publish(msg_SIGIN_BOARD0_STEPDIR2_POSITION);


        ros::spinOnce();
        loop_rate.sleep();
    }

    return 0;
}
